import { useState, useEffect } from "react"
import { BrowserRouter as Router, Navigate, Route, Routes, useNavigate } from 'react-router-dom'
import Merchandises from "./Component/Merchandises"
import Home from "./Component/Home";
import CheckOut from "./Component/CheckOut";
import ThankYou from "./Component/ThankYou";
import './App.css';

function App() {
  const [merchandises, setMerchandises] = useState([])
  const [baskets, setBaskets] = useState([])
  const [showCheckOut, SetshowCheckOut] = useState(false)
  const [totalMoney, setTotalMoney] = useState(0.0)
  const [currentRate, setCurrentRate] = useState(1.00)
  let navigate = useNavigate();

  const currencyRate = [
    { label: "USD", value: 1.00 },
    { label: "NZ", value: 1.55 },
    { label: "AUD", value: 1.41 }
  ]

  useEffect(() => {
    const getMerchandises = async () => {
      const dataFromServer = await fetchMerchandises()
      setMerchandises(dataFromServer)

      // the basket items to the user
      setBaskets(dataFromServer.map(m => m.Count !== 0 ? { ...m, Count: 0 } : m))
    }
    getMerchandises()
  }, [])

  const fetchMerchandises = async () => {
    const res = await fetch(process.env.REACT_APP_API + 'Merchandise')
    const data = await res.json()
    return data
  }

  const addToCounter = async (id) => {
    setBaskets(baskets.map(m => m.Id === id ? { ...m, Count: m.Count + 1 } : m))

    if (showCheckOut) {
      const data = await fetchTotalMoney()
      setTotalMoney(data);
    }
  }

  const substractFromCounter = (id) => {
    setBaskets(baskets.map(m => m.Id === id && m.Count > 0 ? { ...m, Count: m.Count - 1 } : m))
  }

  const fetchTotalMoney = async () => {
    const res = await fetch(process.env.REACT_APP_API + 'Merchandise', {
      method: 'POST',
      headers: { 'Content-type': 'application/json' },
      body: JSON.stringify(baskets.filter((basket) => basket.Count !== 0))
    })

    const data = await res.json()
    return data
  }

  const buttonToggle = async () => {
    // The checkout page will call a backend to calculate the total shipping cost
    if (!showCheckOut) {
      const data = await fetchTotalMoney()
      setTotalMoney(data);
    } else {
      setBaskets(baskets.map(m => m.Count > 0 ? { ...m, Count: 0 } : m));
    }
    SetshowCheckOut(!showCheckOut)

    if (showCheckOut) {
      navigate('/thankyou')
    }
  }

  const changeCurrencyRate = selectedOption => {
    setCurrentRate(parseFloat(selectedOption[0].value).toFixed(2))
    setBaskets(baskets.map(m => { return { ...m, Price: m.Price * 1.2 }; }));
  }

  return (
    <>
      <div className="container">
        <Routes>
          <Route path='/' element={
            <>
              <Home showCheckOut={showCheckOut} buttonToggle={buttonToggle} currencyRate={currencyRate} changeCurrencyRate={changeCurrencyRate} />
              {!showCheckOut && (merchandises.length > 0 ? (<Merchandises merchandises={merchandises} substractFromCounter={substractFromCounter} addToCounter={addToCounter} />) : ("No Merchandises to show"))}
              {showCheckOut && (baskets.length > 0 ? (<CheckOut baskets={baskets} substractFromCounter={substractFromCounter} addToCounter={addToCounter} showCheckOut={showCheckOut} totalMoney={totalMoney} />) : ("No Items to show in baskets"))}
            </>
          } />
          <Route path='/thankyou' element={<ThankYou />} />
        </Routes>
      </div>
    </>
  );
}

export default App;
