import { useState, useEffect, createContext, useContext } from "react"
import React from 'react'
import Merchandise from './Merchandise'

const UserContext = createContext();

const Merchandises = ({ substractFromCounter, addToCounter }) => {

    const merchandises = useContext(UserContext);
    return (
        <>
            {merchandises.map((merchandise, index) => (
                < Merchandise key={index} merchandise={merchandise} substractFromCounter={substractFromCounter} addToCounter={addToCounter} ></Merchandise>
            ))
            }
        </>
    )
}

export default Merchandises
