import Button from './Button';
import Select from 'react-dropdown-select'

const Home = ({ title, showCheckOut, buttonToggle, changeCurrencyRate }) => {
    const currencyRate = [
        { label: "USD", value: 1.00 },
        { label: "NZ", value: 1.55 },
        { label: "AUD", value: 1.41 }
    ]

    const defaultSelected = { label: "USD", value: 1.00 }

    return (
        <>
            <div className='header'>
                <h1>{title}</h1>
            </div>
            <div>
                <h4>
                    <Select options={currencyRate} defaultValue={defaultSelected} onChange={changeCurrencyRate} />
                    <Button className='btn' color={showCheckOut ? 'red' : 'green'} text={showCheckOut ? 'Order' : 'Go To Basket'} onClick={buttonToggle} /></h4>
            </div>

        </>
    )
}

Home.defaultProps = {
    title: 'Merchandises'
}

export default Home
