import React from 'react'
import { useState, useEffect, createContext, useContext } from "react"
import Merchandise from './Merchandise'

const UserContext = createContext();

const CheckOut = ({ substractFromCounter, addToCounter }) => {
    const baskets = useContext(UserContext);
    return (
        <>
            {baskets.map((basket, index) => (
                basket.Count > 0 ? < Merchandise key={index} merchandise={basket} substractFromCounter={substractFromCounter} addToCounter={addToCounter} ></Merchandise> : ''
            ))}
        </>
    )
}

export default CheckOut
