import React from 'react'
import { useState } from 'react'

const Merchandise = ({ merchandise, substractToCounter, addToCounter }) => {
    const [count, setCount] = useState(0)

    // const addToCounter = () => {
    //     if (count >= 5) {
    //         alert("There is no stock available.")
    //     } else {
    //         setCount(count + 1)
    //     }
    // }

    // const substractToCounter = () => {
    //     if (count <= 0) {
    //         alert("You have not selected any stock.")
    //     } else {
    //         setCount(count - 1)
    //     }
    // }

    return (
        <div>
            <h3>
                <label className='label-width'>{merchandise.Name}</label>

            </h3>
            <h4>
                <button className='float:left;' data-testid='substract-btn' onClick={substractToCounter(merchandise.Id)}>-</button>
                <input className='text-center ' data-testid='input' type="number" value={count}
                    onChange={(e) => {
                        setCount(parseInt(e.target.value))
                    }}
                />
                <button className='float:right;' data-testid='add-btn' onClick={addToCounter(merchandise.Id)}>+</button>
            </h4>
            <p>
                {merchandise.Price}$
            </p>
        </div>
    )
}

export default Merchandise
