import React from 'react'
import { useState } from 'react'

const Merchandise = ({ merchandise }) => {
    const [count, setCount] = useState(0)

    const addToCounter = () => {
        if (count >= 5) {
            alert("There is no stock available.")
        } else {
            setCount(count + 1)
        }
    }

    return (
        <div>
            <h3>
                <label className='label-width'>{merchandise.Name}</label>
                <button data-testid='substract-btn' >-</button>
                <input className='text-center ' data-testid='input' type="number" value={count}
                    onChange={(e) => {
                        setCount(parseInt(e.target.value))
                    }}
                />
                <button className='float:right;' data-testid='add-btn' onClick={addToCounter}>+</button>
            </h3>
            <p>
                {merchandise.Price}$
            </p>
        </div>
    )
}

export default Merchandise
